import React from 'react';
import {Link} from 'react-router-dom';


class ShsCourses extends React.Component{

    constructor(props){
        super(props);
        this.state={
            shscourses:[],


        }
    }



    componentDidMount(){
        fetch("http://127.0.0.1:5000/shs_courses",{
           methods:"GET",
            "Content-Type":"application/json"
        })
        .then(res=>res.json())
        .then(data=>{
            this.setState({
                shscourses:data,


            })
        })

    }
















    render(){
        return(
            <div className="row course-background">
            {
                this.state.shscourses.map(t=>{
                      return(

                          <div className="course-container col-md-6 col-xl-4 col-sm-12 col-xs-12 col-lg-3">

                          <div className="course-header"> 

                          <img className="course-img" src={`http://127.0.0.1:5000${t.course_img}`}/>

                          </div>

                          <div className="course-footer  col text-center"><h3 className="course-name">{t.name}</h3>
                          <Link to={`coursedescription/${t.id}`}><button className="enroll">Description</button></Link>
                          <Link  to={`singlecourse/${t.id}`} style={{marginLeft:30}}><button className="enroll">Content</button></Link>
                          </div>
                      </div>

                      )
                })

            }
        </div>

     )
}

}
export default ShsCourses;
