/*e.preventDefault() function in any line will prevent
the the form from submitting automatically
*/


import React from 'react';
import InstructorLogin from './InstructorLogin';
import './Editprofile.css';

class Editprofile extends React.Component{


constructor(props){
    super(props);
    this.state={
        instructor_img:null,
        bio:"",
        name:"",
        nameerr:"",
        bioerr:"",
        imgerr:"",
        loading:false,

    }
}











filechangehandler=(event)=>{
    this.setState({instructor_img:event.target.files[0]})
  }

  uploadhandler=(e)=>{



   e.preventDefault();
     /*I'm checking to see wether the bio is empty or not
    If it's  empty, we throw error message*/
   if(this.state.instructor_img==null){
    this.setState({
        imgerr:"Photo Required"
    })
}
   else{
   //Otherwise we send the image to the server

   this.setState({
    loading:true
})


   const formData=new FormData();
   formData.append("instructor_img",this.state.instructor_img)

   fetch("http://127.0.0.1:5000/instructor_update_profile_img",
   {
     method:"POST",
     body:formData,
     headers:{
         Authorization:`${localStorage.getItem("instructor_token")}`
     }

   }
   )
   .then(res=>{
       alert("Profile photo updated");
       this.setState({
           loading:false
       })
    })
   .catch(err=>{alert("Network Error")})
}
  }






biohandler=(e)=>{
    this.setState({
        bio:e.target.value
    })
}



submitbio=(e)=>{
    e.preventDefault();
    /*I'm checking to see wether the bio is empty or not
    If it's  empty, we throw error message*/
    if (this.state.bio.trim()===""){
        this.setState({
            bioerr:"Bio Required"
         })
    }
    else{
        //Otherwise,we send data to the server

    fetch("http://127.0.0.1:5000/instructor_update_bio",{
        method:"POST",
         body:JSON.stringify(this.state.bio),
         "Content-type":"application/json",



    headers:{

        Authorization:`${localStorage.getItem("instructor_token")}`,

    }
}
    )
    .then(res=>res.json())
    .then(data=>{
        alert(data.message)
    })
    .catch(err=>{alert("No Network")})
}

}



namehandler=(e)=>{
    this.setState({
        name:e.target.value
    })
}




submitname=(e)=>{
    e.preventDefault();
        /*I'm checking to see wether the name is empty or not
    If it's  empty, we throw error message*/
    if (this.state.name.trim()===""){
        this.setState({
            nameerr:"Name Required"
         })
    }
    else{


      //Otherwise we send data to the server for processing

      this.setState({
        loading:true
    })

    fetch("http://127.0.0.1:5000/instructor_update_name",{
        method:"POST",
        "Content-Type":"Application/json",
         body:JSON.stringify(this.state.name),


    headers:{
        Authorization:`${localStorage.getItem("instructor_token")}`
    }
}
    )
    .then(res=>res.json())
    .then(data=>{
        alert(data.message);
        this.setState({
            loading:false
        })
    }).catch(err=>{console.log("No Network")})
} 
}









  render(){
      return(
          <div>
          <div style={{flexDirection:"row"}} className="col text-center">
          <h3 style={{marginTop:100,textAlign:"center",fontSize:20}}>Update profile picture</h3>
          <input onChange={this.filechangehandler} type="file" name="instructor_img" className="inputfile1"/>
          {this.state.loading==false?<button onClick={this.uploadhandler} className="upload-button">Upload</button>:
          <button onClick={this.uploadhandler} className="upload-button">Uploading....</button>
        }
          <h3 style={{color:"red",fontSize:18}}>{this.state.imgerr}</h3>
          </div>







          <div style={{flexDirection:"row"}} className="col text-center">
          <h3 style={{marginTop:30,textAlign:"center",fontSize:20}}>Update name</h3>
          <input  placeholder="Name"value={this.state.name} onChange={this.namehandler} type="text" name="name" className="nameinput"/>
          {this.state.loading==false?<button onClick={this.submitname} className="namebutton">Update</button>:
          <button onClick={this.submitname} className="namebutton">Updating....</button>
          }
          <h3 style={{color:"red",fontSize:18}}>{this.state.nameerr}</h3>
          </div>


          <div style={{flexDirection:"row"}} className="col text-center">
          <h3 style={{marginTop:30,textAlign:"center",fontSize:20}}>Update Bio</h3>
          <textarea required="true" placeholder="My Bio"value={this.state.bio} onChange={this.biohandler} type="text" name="bio" className="nameinput"/>
          <button onClick={this.submitbio} className="namebutton">Update</button>
          <h3 style={{color:"red",fontSize:18}}>{this.state.bioerr}</h3>
          </div>




          <div style={{flexDirection:"row"}} className="col text-center">
          <button className="logoutbutn" onClick={()=>{localStorage.removeItem("instructor_token");this.props.history.push("InstructorLogin")}}>Logout Account</button>
          </div>







          </div>
      )
  }

}

export default Editprofile;
