import "./ViewQuestion.css";

class ViewQuestions extends React.Component{



   constructor(props){
       super(props);
       this.state={
           questions:[]
       }
   }




   componentDidMount(){
    fetch(`http://127.0.0.1:5000/view_questions/${this.props.match.params.id}`,
    {
        method:"GET",
        "Content-Type":"application/json"
    }
    )
    .then(res=>res.json())
    .then(data=>{
        this.setState({
            questions:data
        })
    })
    .catch(err=>console.log(err))
}






    render(){
        return(
