import React,{useState} from 'react';
import './QuestionBox.css';

const QuestionBox=({question,options})=>{
    const [answer,setAnswer]=useState(options)
    return(

        <div className="row">
        <div className="profile-background">

    <div style={{marginTop:20,marginBottom:20}}  className="col-lg-7 col-xs-10 col-xl-7  col-sm-10 col-md-10 profile-container  "> 

    <ul>
        <li  style={{marginLeft:10}}>
            <h3 className="float-left question-text">{question}</h3>
        </li>
    </ul>
    {
        answer.map((text,index)=>
        <div className="answer-container" style={{display:"flex",flexDirection:"column"}}>
        <div  className="option-container"><h3 style={{fontSize:15}} className="float-left">{options}</h3></div>
        </div>
        )
    }


      </div>










</div>     
</div>
    )
}

export default QuestionBox;
