import React from 'react';


class TakeQuiz extends React.Component{

constructor(props){
    super(props);
    this.state={
        userAnswer:null,
        currentIndex:0,
        options:[],
        score:0,
        quiz_end:false,
        disabled:true//Disables next button if user is yet to select an option

    }


    }



//We load the questions from the server if component mounts

componentDidMount(){
    const {currentIndex}=this.state;
    fetch(`http://127.0.0.1:5000/take_quiz/${this.props.match.params.id}`,
    {
        method:"GET",
        "Content-Type":"application/json",
    }
    )
    .then(res=>res.json())
    .then(data=>{
       this.setState(()=>{
           return{
               question:data[this.state.currentIndex].question,
               correct_answer:data[this.state.currentIndex].correct_answer,
               options:data[this.state.currentIndex].options
           };
       })

    })
    .catch(err=>console.log(err))
}














     //We need to know the user's answer,the correct answer and the score.

     next_question_handler=()=>{
        const {userAnswer,correct_answer,score}=this.state;

        //Comparing the userAnswer with the actual correct answer

        if(userAnswer===correct_answer){
            this.setState({
                score:score+1
            })

            this.setState({
                currentIndex:this.state.currentIndex+1,
                userAnswer:null,
            })
            console.log(this.state.currentIndex);
        } 


    }










//Function to check check if the student answered  so that we enable or disable next function

checkAnswer=(answer)=>{
    this.setState({
        userAnswer:answer,
        disabled:false,
    })
}





finish_handler=()=>{
    if(this.state.currentIndex===this.state.questions.length-1){
        this.setState({
            quiz_end:true
        })
    }
}












/*Now we update the component................
when the compinent updates or when the currrent index changes,
we have to set the questions and also disable the disable the
options so that the user or student will not click on the options.

When the component updates,we are going to load another question and disables
the next button until user or student selects an option
*/


componentDidUpdate(Prevprops,prevState){
    const {currentIndex}=this.state;
    if(this.state.currentIndex!=preVstate.currentIndex){
       fetch(`http://127.0.0.1:5000/take_quiz/${this.props.match.params.id}`,
       {
           method:"GET",
           "Content-Type":"application/json",
       }
       )
       .then(res=>res.json())
       .then(data=>{
         this.setState(()=>{
             return{

             };
         })
       })
       .catch(err=>console.log(err))
    }

}



//Now we render the data into the  UI

render(){
    return(
        <div>
    <h1 style={{marginTop:300}}>{this.state.question}</h1>
   <span>
        {`question${this.state.currentIndex+1}`} of {this.state.questions.length}</span>

        {
       this.state.options.map(t=>
        //We create a style conditionallly determining if userAnswer is selected or not
        <p key={t.id} className={`options ${this.state.userAnswer==t?"selected":null}`}
        onClick={()=>{this.checkAnswer(t)}}
        >
         {t}
        </p>

                )

        }


       {/************We are going to display next button conditionally********/}

       {this.state.currentIndex<this.state.questions.length-1&&
       <button disabled={this.state.disabled} onClick={()=>this.next_question_handler}>
            Next Question
            </button>}

            {this.state.currentIndex==this.state.questions.length-1&&
            <button onClick={()=>this.finish_handler} disabled={this.state.disabled}>
                Finish
                </button>
            }



        </div>
    )
}








}

export default TakeQuiz;
