import React from 'react';
import './Signup.css';
import {Link} from 'react-router-dom';


//I'm using this for email validation
const validEmailRegex= 
RegExp(/^(([^<>()\[\]\.,;:\s@\"]+(\.[^<>()\[\]\.,;:\s@\"]+)*)|(\".+\"))@(([^<>()[\]\.,;:\s@\"]+\.)+[^<>()[\]\.,;:\s@\"]{2,})$/i);



//function to validate the form



class StudentSignup extends React.Component{



    constructor(props){
        super(props);
        this.state={
            fullname:"",
            email:"",
            password:"",
            error:"",
            message:"",
            input_errors:{
             name:"",
             email:"",
             password:""
            },
            loading:false

        }
    }

    handleinput=(e)=>{
        e.preventDefault();
        let name=e.target.name;
        let value=e.target.value;
        let errors =this.state.errors;
        /*I'm using switch 
        statement here because
         I want to avoid multiple
        if else statements*/

        switch(name){
            case "fullName":
                errors.fullName=value.length<6
                ?'Full Name must not be less than 6 characters':'';
                break;
                //Here,I'm testing email pattern
            case 'email': 
                errors.email = 
                  validEmailRegex.test(value)
                    ? ''
                    : 'Email is not valid!';
                break; 

            case 'password': 
             errors.password = 
              value.length < 8
               ? 'Password must be at least characters long!'
               : '';
            break;
         default:
         break;
        }

        this.setState({errors, [name]: value}, ()=> {
            //So that I can see the result in the conssole
            console.log(errors)
        })

    }





    signup=(e)=>{
        e.preventDefault();


          this.setState({
              loading:true
          })


        fetch(" http://127.0.0.1:5000/student_signup",{
            method:"POST",
            body:JSON.stringify(this.state),
            headers:{
                'Content-type':'application/json'
            }
        })
        .then(res=>res.json())
        .then(data=>{
            this.setState({
                loading:false
            })

            if (data.error==null){
                alert(data.message);
                this.props.history.push("StudentLogin")

            }
            else{
                this.setState({error:data.error})
            }
        }).catch((err)=>{alert(err)});



    }







    render(){
        return(

            <div className="row">
            <div className="bg">

                  <div className="form-container ">
                      <form onSubmit={this.signup} className="form-group">
                      <h3 style={{color:"grey",fontSize:20}}>Signup for Wap</h3>
                          <input type="text" onChange={this.handleinput}
                          className=" forminput"
                          placeholder="Full name"
                          name="fullname"
                          value={this.state.fullname}
                          />
                          <h3 style={{color:"red",fontSize:15}}>{this.state.nameerr}</h3>

                       <input type="text" onChange={this.handleinput}
                          className=" forminput"
                          placeholder="Email"
                          name="email"

                          value={this.state.email}
                          />
                            <h3 style={{fontSize:15,color:"red"}}>{this.state.error}</h3>
                            <h3 style={{color:"red",fontSize:15}}>{this.state.nameerr}</h3>

                       <input type="password" onChange={this.handleinput}
                          className=" forminput"
                          placeholder="Password"
                          name="password"
                          value={this.state.password}
                          />
                          <h3 style={{color:"red",fontSize:15}}>{this.state.passworderr}</h3>

                          {this.state.loading==false?<input type="submit" className="formbutton" value="Signup"/>:<input type="submit" className="formbutton" value="Sending..."/>}

                          <h3 style={{fontSize:15}}>
                              Already have an account?
                          <Link to="/studentlogin">
                            <a  style={{fontSize:15,color:"rgb(184, 23, 103)"}}>
                              Login
                              </a> 
                              </Link>
                              </h3>
                      </form>


            </div>
         </div>
         </div>



        )
    }
}

export default StudentSignup;
