import React from 'react';
import {Document,Page} from 'react-pdf';
import { wrap } from 'module';


class SinglePdf extends React.Component{
    constructor(props){
        super(props);
        this.state={
            pdf:[]
        }
    }


   componentDidMount(){
       fetch(`http://127.0.0.1:5000/view_pdfs/${this.props.match.params.id}`,
       {
           method:"GET"
       }
       )
       .then(res=>res.json())
       .then(data=>{
           this.setState({
               pdf:data
           })
       })
   }





    render(){
        return(



           <div>
               {
                   this.state.pdf.map(t=>
                    <Document>
                        <Page wrap={false}/>
                    </Document>
                    )
               }
           </div>

        )
    }



}

export default SinglePdf;
