import React from 'react';
import QuestionBox from './Components/QuestionBox';

class TakeQuiz extends React.Component{

constructor(props){
    super(props);
    this.state={
        questions:[]
    }
}

getQuestions=()=>{
    fetch(`http://127.0.0.1:5000/take_quiz/${this.props.match.params.id}`,
    {
        method:"GET",
        "Content-Type":"application/json"
    }
    )
    .then(res=>res.json())
    .then(data=>{
        this.setState({
            questions:data
        })
    })
}


componentDidMount(){
    this.getQuestions();
}



render(){
    return(
          <div>
              {
                  <div>
                  this.state.questions.map(
                      ({question,options,correct_answer,id})=>
                      <QuestionBox 
                      question={question}
                     options={options}
                     key={id}
                     />
                  )

                  <div style={{display:"flex",flexDirection:"column"}}>
                  <div  className="option-container"><h3 style={{fontSize:15}} className="float-left">{options[0]}</h3></div>
                 </div>
               </div>  
              }
          </div>
    )
}



}

export default TakeQuiz;
