import React from 'react';
import QuestionBox from './Components/QuestionBox';

class TakeQuiz extends React.Component{

constructor(props){
    super(props);
    this.state={
        questions:[]
    }
}

componentDidMount(){
    fetch(`http://127.0.0.1:5000/take_quiz/${this.props.match.params.id}`,
    {
        method:"GET",
        "Content-Type":"application/json"
    }
    )
    .then(res=>res.json())
    .then(data=>{
        this.setState({
            questions:data
        })
        //For testing
        console.log(data)
    })



}




render(){
    return(
          <div>
              {
                  this.state.questions.map(
                      ({question,options,correct_answer,id})=>
                      (<QuestionBox style={{marginTop:-300}}
                      question={question} options={options} key={id}
                      />)
                  )
              }
          </div>
    )
}






}
export default TakeQuiz;
