import React from 'react';
import './TakeQuiz.css';

class TakeQuiz extends React.Component{

constructor(props){
    super(props);
    this.state={
        questions:[]
    }
}

componentDidMount(){
    fetch(`http://127.0.0.1:5000/take_quiz/${this.props.match.params.id}`,
    {
        method:"GET",
        "Content-Type":"application/json"
    }
    )
    .then(res=>res.json())
    .then(data=>{
        this.setState({
            questions:data
        })
    })



}






}
export default TakeQuiz;
