import React from 'react';

class TakeQuiz extends React.Component{

constructor(props){
    super(props);
    this.state={
        questions:[]
    }
}

getQuestions=()=>{
    fetch(`http://127.0.0.1:5000/take_quiz/${this.props.match.params.id}`,
    {
        method:"GET",
        "Content-Type":"application/json"
    }
    )
    .then(res=>res.json())
    .then(data=>{
        this.setState({
            questions:data
        })
    })
}


componentDidMount(){
    this.getQuestions();
}




}

export default TakeQuiz;
